$(document).ready(function () {

  // crasual for rated near you

  var multiCarousel = document.querySelector(
    "#carouselExampleControls"
  );
  if (window.matchMedia("(min-width: 576px)").matches) {
    var carousel = new bootstrap.Carousel(multiCarousel, {
      interval: false,
      wrap: false
    });
    var carsulWidth = $(".carousel-inner")[0].scrollWidth;
    var cardWidth = $(".carousel-item").width();
    var scrollPosition = 0;
    $("#carouselExampleControls .carousel-control-next").on("click", function () {
      console.log(carsulWidth);
      console.log(cardWidth);
      console.log(scrollPosition);
      if (scrollPosition < carsulWidth - cardWidth * 3) {
        scrollPosition += cardWidth;
        $("#carouselExampleControls .carousel-inner").animate(
          { scrollLeft: scrollPosition },
          600
        );
      }
    });
    $("#carouselExampleControls .carousel-control-prev").on("click", function () {
      console.log(carsulWidth);
      console.log(cardWidth);
      console.log(scrollPosition);
      
      if (scrollPosition > 1) {
        scrollPosition -= cardWidth;
        $("#carouselExampleControls .carousel-inner").animate(
          { scrollLeft: scrollPosition },
          600
        );
      }
    
    });
  } else {
    $(multipleCardCarousel).addClass("slide");
  }

});


